import {useEffect, useState} from 'react'
import Nav from './views/Nav'  
import axios from "axios";
import './App.css';

function App() {

  const [data, setData] = useState([])

  const options = {
    method: 'GET',
    url: 'https://simpleanime.p.rapidapi.com/anime/search/sword%20art%20online',
    headers: {
      'x-rapidapi-key': 'cc687eba84mshcc7485fcf110baap193a15jsnfb1be463a74d',
      'x-rapidapi-host': 'simpleanime.p.rapidapi.com'
    }
  };


  useEffect(() => {
    axios
    .request(options)
    .then(function (response) {
      setData(data)
      console.log('Initial Data Grab from the API', response.data.data);
    })
    .catch(function (error) {
      console.error(error);
    });
  }, [true])


  return (
    <div className="App">
    <Nav data={data}/>  
    {data && data.map((item, idx) => {
      return (
        <div key={idx} style={{maxWidth: '30vw'}}>
          {/* <img src={data[idx].cover} alt={data[idx].title} /> */}
          <p>{data[idx].title}</p>
        </div>
      )
    })} 

    </div>
  );
}

export default App;
